export default function filterNegativeReviews(input) {
    const isNegativeReview = (review) => {
        // Assuming negative reviews contain specific keywords
        const negativeKeywords = ['negativ', 'bad', 'neutral'];
        return negativeKeywords.some(keyword => review.toLowerCase().includes(keyword));
    };

    if (Array.isArray(input)) {
        return input.filter(review => !isNegativeReview(review));
    } else if (typeof input === 'string') {
        return isNegativeReview(input) ? input : undefined;
    }
    return undefined;
}
